mapper.select:
select
*
from ${prefix}_def_processdefinition
where PROCESS_ID =#{processId,jdbcType=VARCHAR}

	AND TENANT_ID=${appId}

select * from
${prefix}_DEF_PROCESSDEFINITION
where DEPLOYMENT_ID = #{deploymentId,jdbcType=VARCHAR} 
AND PROCESS_KEY =#{key,jdbcType=VARCHAR}

	AND TENANT_ID=${appId}

select * from
${prefix}_DEF_PROCESSDEFINITION
where PROCESS_KEY = #{key,jdbcType=VARCHAR} 
AND VERSION =#{version,jdbcType=INTEGER}

	AND TENANT_ID=${appId}

mapper.sql:
from ${prefix}_DEF_PROCESSDEFINITION RES LEFT JOIN
${prefix}_DEF_DEPLOYMENT DP ON RES.DEPLOYMENT_ID = DP.ID


		RES.PROCESS_ID = #{id}


		and RES.CATEGORY = #{category}


		and RES.CATEGORY like #{categoryLike}


		and RES.PROCESS_NAME = #{name}


		and RES.PROCESS_NAME like #{nameLike}


		and RES.PROCESS_KEY = #{key}


		and RES.PROCESS_KEY like #{keyLike}


		and RES.VERSION = #{version}


		and RES.DEPLOYMENT_ID = #{deploymentId}


		and RES.VERSION = (select max(VERSION) from
		${prefix}_DEF_PROCESSDEFINITION where KEY = RES.KEY)


		and RES.TENANT_ID=${tenantId}
